parameters.jsCode:
// Validate order creation request
const body = $input.item.json.body;

// Required fields validation
const requiredFields = ['offer_id', 'passengers', 'contacts'];
const missingFields = [];

for (const field of requiredFields) {
  if (!body || !body[field]) {
    missingFields.push(field);
  }
}

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

// Validate passengers array
if (!Array.isArray(body.passengers) || body.passengers.length === 0) {
  throw new Error('passengers must be a non-empty array');
}

// Validate each passenger
for (let i = 0; i < body.passengers.length; i++) {
  const passenger = body.passengers[i];
  const passengerRequired = ['first_name', 'last_name', 'date_of_birth', 'document'];

  for (const field of passengerRequired) {
    if (!passenger[field]) {
      throw new Error(`Passenger ${i + 1}: missing ${field}`);
    }
  }

  // Validate document
  if (!passenger.document.type || !passenger.document.number) {
    throw new Error(`Passenger ${i + 1}: invalid document data`);
  }
}

// Validate contacts
if (!body.contacts.email || !body.contacts.phone) {
  throw new Error('contacts must include email and phone');
}

return {
  offer_id: body.offer_id,
  passengers: body.passengers,
  contacts: body.contacts,
  payment_method: body.payment_method || 'CARD',
  metadata: body.metadata || {}
};

