// Transform order creation response
const response = $input.item.json;

return {
  order_id: response.order_id || response.id,
  status: response.status || 'CREATED',
  pnr: response.pnr || null,
  price: {
    total: response.price?.total || 0,
    currency: response.price?.currency || 'RUB'
  },
  passengers: response.passengers || [],
  segments: response.segments || [],
  payment: {
    method: response.payment?.method || 'CARD',
    status: response.payment?.status || 'PENDING',
    deadline: response.payment?.deadline || null
  },
  created_at: response.created_at || new Date().toISOString(),
  expires_at: response.expires_at || null,
  metadata: {
    processed_at: new Date().toISOString(),
    workflow_id: 'drct_order_create'
  }
};

parameters.responseBody:
={{ JSON.stringify({
  error: {
    code: 'VALIDATION_ERROR',
    message: $json.message || 'Invalid order data',
    details: $json
  }
}) }}

