parameters.jsCode:
// Validate price request
const body = $input.item.json.body;

if (!body) {
  throw new Error('Request body is required');
}

if (!body.offer_id) {
  throw new Error('Missing required field: offer_id');
}

if (!Array.isArray(body.passengers) || body.passengers.length === 0) {
  throw new Error('passengers array is required and cannot be empty');
}

// Validate each passenger has required fields
for (let i = 0; i < body.passengers.length; i++) {
  const passenger = body.passengers[i];

  if (!passenger.type) {
    throw new Error(`Passenger ${i + 1}: type is required (ADT, CHD, INF)`);
  }

  // For price requests, passengers should have type at minimum
  // Full details (documents, etc.) may be optional depending on DRCT requirements
}

console.log(`[Validate] Price request for offer: ${body.offer_id}`);
console.log(`[Validate] Passengers: ${body.passengers.length}`);

return { 
  offer_id: body.offer_id,
  passengers: body.passengers
};

// Transform DRCT price response to Aviaframe format
const response = $input.item.json;

console.log('[Transform] DRCT price response received');

return {
  offer_id: response.offer_id || response.id,
  price: {
    total: response.price?.total || 0,
    currency: response.price?.currency || 'USD',
    breakdown: {
      base_fare: response.price?.base_fare || response.price?.base || 0,
      taxes: response.price?.taxes || 0,
      fees: response.price?.fees || 0,
      surcharges: response.price?.surcharges || 0
    },
    per_passenger: response.price?.per_passenger || []
  },
  expiration: response.expiration || response.expires_at || null,
  rules: response.rules || {},
  fare_details: response.fare_details || {},
  timestamp: new Date().toISOString()
};

