parameters.jsCode:
// Transform Aviaframe format to DRCT API format
const body = $input.item.json.body;

if (!body) {
  throw new Error('Request body is required');
}

// Validate required fields
if (!body.origin || !body.destination || !body.depart_date) {
  throw new Error('origin, destination, and depart_date are required');
}

// Ensure date format is YYYY-MM-DD
function formatDate(dateStr) {
  if (!dateStr) return null;
  // If already in YYYY-MM-DD format, return as is
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateStr)) {
    return dateStr;
  }
  // Try to parse and format
  const date = new Date(dateStr);
  if (isNaN(date.getTime())) {
    throw new Error(`Invalid date format: ${dateStr}`);
  }
  return date.toISOString().split('T')[0];
}

// Build flights array
const flights = [];

// Outbound flight
flights.push({
  departure_airport_code: body.origin.toUpperCase(),
  arrival_airport_code: body.destination.toUpperCase(),
  departure_date: formatDate(body.depart_date)
});

// Return flight (if provided)
if (body.return_date) {
  flights.push({
    departure_airport_code: body.destination.toUpperCase(),
    arrival_airport_code: body.origin.toUpperCase(),
    departure_date: formatDate(body.return_date)
  });
}

// Build passengers array
const passengers = [];

// Add adults (default to 1 if not provided)
const adults = body.adults || 1;
for (let i = 0; i < adults; i++) {
  passengers.push({ type: "ADT" });
}

// Add children
const children = body.children || 0;
for (let i = 0; i < children; i++) {
  passengers.push({ type: "CHD" });
}

// Add infants
const infants = body.infants || 0;
for (let i = 0; i < infants; i++) {
  passengers.push({ type: "INF" });
}

// Build DRCT request
const drctRequest = {
  flights: flights,
  passengers: passengers
};

// Add filters if cabin_class is provided
if (body.cabin_class) {
  drctRequest.filters = {
    cabin_class: body.cabin_class.toLowerCase()
  };
}

console.log('[Transform] Converted to DRCT format:', JSON.stringify(drctRequest, null, 2));

return drctRequest;

// Transform DRCT API response to Aviaframe format
const response = $input.item.json;

console.log('[Transform] DRCT response received');

// Extract search results
const offers = response.offers || [];

console.log(`[Transform] Processing ${offers.length} offers`);

// Transform each offer to Aviaframe format
const transformedOffers = offers.map(offer => ({
  id: offer.id,
  price: {
    total: offer.price?.total || 0,
    currency: offer.price?.currency || 'USD',
    base: offer.price?.base || 0,
    taxes: offer.price?.taxes || 0
  },
  segments: offer.segments?.map(segment => ({
    origin: segment.origin,
    destination: segment.destination,
    departure: segment.departure,
    arrival: segment.arrival,
    carrier: segment.carrier,
    flight_number: segment.flight_number,
    aircraft: segment.aircraft,
    duration: segment.duration
  })) || [],
  baggage: offer.baggage || {},
  fare_family: offer.fare_family || 'ECONOMY',
  available_seats: offer.available_seats || 0
}));

return {
  search_id: response.search_id || 'unknown',
  offers: transformedOffers,
  metadata: {
    total_offers: transformedOffers.length,
    timestamp: new Date().toISOString(),
    api_version: response.api_version || '1.0'
  }
};

